import { Component } from "react";
import { Carousel, Col, Container, Row } from "react-bootstrap";
import "../Components/Netflix.css";
import Movie from "./Movie";

class MovieSlider extends Component {
  state = {
    film: [],
  };
  getMovies = async () => {
    try {
      let response = await fetch("https://www.omdbapi.com/?i=tt3896198&apikey=874ae9ca&s=Harry%20Potter");

      if (response.ok) {
        const data = await response.json();
        const movieArray = data.Search;
        console.log(movieArray);
        this.setState({ film: movieArray });
      }
    } catch (error) {
      console.log(error);
    }
  };

  componentDidMount() {
    this.getMovies();
  }

  render() {
    return (
      <Carousel>
        <Carousel.Item>{this.state.film.map((movie) => ({}))}</Carousel.Item>
      </Carousel>
    );
  }
}

export default MovieSlider;
